import proj4 from "proj4";

export async function fetchRealStations() {
  try {
    const response = await fetch("http://localhost:4000/api/charging");
    const json = await response.json();

    const items = json?.response?.body?.items?.item;
    if (!items) return [];

    // üìå Safemap Ï¢åÌëúÍ≥Ñ = TMÏ§ëÎ∂Ä (EPSG:2097)
    const TM_2097 =
      "+proj=tmerc +lat_0=38 +lon_0=127.0028902777778 +k=1 +x_0=200000 +y_0=500000 +ellps=GRS80 +units=m +no_defs";

    const WGS84 = "+proj=longlat +datum=WGS84 +no_defs";

    return items.map((item) => {
      // 1) ÏõêÎ≥∏ Ï¢åÌëú ÏΩòÏÜî ÌôïÏù∏
      console.log("üìå ÏõêÎ≥∏ Ï¢åÌëú:", item.X, item.Y);

      // 2) TM‚ÜíWGS84 Î≥ÄÌôò
      const [lng, lat] = proj4(TM_2097, WGS84, [
        Number(item.X),
        Number(item.Y),
      ]);

      // 3) Î≥ÄÌôòÎêú Ï¢åÌëú ÌôïÏù∏
      console.log("üéØ Î≥ÄÌôòÎêú Ï¢åÌëú:", lat, lng);

      return {
        id: item.OBJT_ID,
        name: item.STAT_NM,
        address: item.ADRES,
        company: item.BUSI_NM,
        chargerType: item.CHGER_TY,
        status: "available",

        latitude: lat,
        longitude: lng,
      };
    });
  } catch (err) {
    console.error("SafeMap API Error:", err);
    throw new Error("Ïã§Ï†ú API Ìò∏Ï∂ú Ïã§Ìå®");
  }
}

export const EvDataService = {
  async getStations() {
    return await fetchRealStations();
  },

  filterStations(stations) {
    return stations;
  },
};
