// src/components/ev/EvDataService.js

const R = 6378137; // Web Mercator 반지름 (WGS84)

// Safemap X/Y(Web Mercator) -> 위도/경도 변환 함수
function webMercatorToLatLng(x, y) {
  const lon = (x / R) * (180 / Math.PI);
  const lat =
    (2 * Math.atan(Math.exp(y / R)) - Math.PI / 2) * (180 / Math.PI);

  return { lat, lng: lon };
}

// 실제 Safemap 프록시 서버에서 데이터 불러오기
export async function fetchRealStations() {
  try {
    const response = await fetch("http://localhost:4000/api/charging");
    const json = await response.json();

    const items = json?.response?.body?.items?.item;
    if (!items) {
      console.warn("⚠️ Safemap items 없음:", json);
      return [];
    }

    const stations = items
      .map((item) => {
        // 숫자 아닌 경우 버리기
        const x = Number(item.X);
        const y = Number(item.Y);
        if (!x || !y || Number.isNaN(x) || Number.isNaN(y)) {
          return null;
        }

        // 좌표 변환
        const { lat, lng } = webMercatorToLatLng(x, y);

        // 한국 영역이 아닌 이상한 값 걸러내기
        if (lat < 30 || lat > 45 || lng < 120 || lng > 135) {
          // console.log("🚫 이상한 좌표, 스킵:", lat, lng, item.STAT_NM);
          return null;
        }

        return {
          id: item.OBJT_ID,
          name: item.STAT_NM,
          address: item.ADRES,
          company: item.BUSI_NM,
          chargerType: item.CHGER_TY,
          status: "available", // safemap에는 상태값 없음 → 그냥 기본값
          lat,
          lng,
        };
      })
      .filter(Boolean); // null 제거

    console.log("✅ 변환된 스테이션 개수:", stations.length);
    console.log("🧪 예시 스테이션:", stations[0]);

    return stations;
  } catch (err) {
    console.error("SafeMap API Error:", err);
    throw new Error("실제 API 호출 실패");
  }
}

export const EvDataService = {
  async getStations() {
    return await fetchRealStations();
  },

  filterStations(stations, filters) {
    // 지금은 필터 아직 안 쓰니까 그냥 전부 리턴
    return stations;
  },
};
