// src/components/ev/EvDataService.js
import proj4 from "proj4";

export async function fetchRealStations() {
  try {
    const response = await fetch("http://localhost:4000/api/charging");
    const json = await response.json();

    const items = json?.response?.body?.items?.item;
    if (!items) return [];

    // Safemap Ï¢åÌëú Ï≤¥Í≥Ñ (TM Ï§ëÎ∂ÄÏõêÏ†ê)
    const TM_5186 =
      "+proj=tmerc +lat_0=38 +lon_0=127.5 +k=1 +x_0=200000 +y_0=600000 +ellps=GRS80 +units=m +no_defs";

    const WGS84 = "+proj=longlat +datum=WGS84 +no_defs";

    return items.map((item) => {
      const rawX = Number(item.X);
      const rawY = Number(item.Y);

      console.log("üìå ÏõêÎ≥∏ Ï¢åÌëú:", rawX, rawY);

      const [lng, lat] = proj4(TM_5186, WGS84, [rawX, rawY]);

      console.log("üéØ Î≥ÄÌôòÎêú Ï¢åÌëú:", lat, lng);

      return {
        id: item.OBJT_ID,
        name: item.STAT_NM,
        address: item.ADRES,
        company: item.BUSI_NM,
        chargerType: item.CHGER_TY,

        // Leaflet Ïö© ÏúÑÍ≤ΩÎèÑ
        lat,
        lng,

        status: "available",
      };
    });
  } catch (err) {
    console.error("SafeMap API Error:", err);
    throw new Error("Ïã§Ï†ú API Ìò∏Ï∂ú Ïã§Ìå®");
  }
}

export const EvDataService = {
  async getStations() {
    return await fetchRealStations();
  },

  filterStations(stations, filters) {
    return stations; // ÌïÑÌÑ∞ ÏóÜÏùå
  },
};
