// src/components/ev/EvDataService.js

const R = 6378137; // Web Mercator Î∞òÏßÄÎ¶Ñ (WGS84)

// Safemap Web Mercator ‚Üí ÏúÑÎèÑ/Í≤ΩÎèÑ Î≥ÄÌôò
function webMercatorToLatLng(x, y) {
  const lon = (x / R) * (180 / Math.PI);
  const lat =
    (2 * Math.atan(Math.exp(y / R)) - Math.PI / 2) * (180 / Math.PI);

  return { lat, lng: lon };
}

// Safemap API ÌîÑÎ°ùÏãú Ìò∏Ï∂ú
export async function fetchRealStations() {
  try {
    const response = await fetch("http://localhost:4000/api/charging");
    const json = await response.json();

    const items = json?.response?.body?.items?.item;
    if (!items) {
      console.warn("‚ö†Ô∏è Safemap items ÏóÜÏùå:", json);
      return [];
    }

    // ------------------------------------------------------------
    // üî• STAT_ID Í∏∞Ï§ÄÏúºÎ°ú Ï∂©Ï†ÑÏÜå Î≥ëÌï© Íµ¨Ï°∞ ÏÉùÏÑ±
    // ------------------------------------------------------------
    const merged = {};

    for (const item of items) {
      const x = Number(item.X);
      const y = Number(item.Y);

      // Ï¢åÌëú Ïú†Ìö®ÏÑ± Í≤ÄÏÇ¨
      if (!x || !y || Number.isNaN(x) || Number.isNaN(y)) continue;

      // Ï¢åÌëú Î≥ÄÌôò
      const { lat, lng } = webMercatorToLatLng(x, y);

      // ÌïúÍµ≠ Î≤îÏúÑ Î≤óÏñ¥ÎÇòÎ©¥ Ïä§ÌÇµ
      if (lat < 30 || lat > 45 || lng < 120 || lng > 135) continue;

      const key = item.STAT_ID;

      // Ï∂©Ï†ÑÏÜå ÏµúÏ¥à ÏÉùÏÑ±
      if (!merged[key]) {
        merged[key] = {
          id: key,
          name: item.STAT_NM,
          address: item.ADRES,
          company: item.BUSI_NM,
          lat,
          lng,
          chargers: [], // üî• Ï∂©Ï†ÑÍ∏∞ Î™©Î°ù Î≥ëÌï©
        };
      }

      // Ï∂©Ï†ÑÍ∏∞ Ï∂îÍ∞Ä
      merged[key].chargers.push({
        chargerId: item.CHGER_ID,
        chargerType: item.CHGER_TY,
        useTime: item.USE_TM,
        tel: item.TELNO,
      });
    }

    const stationList = Object.values(merged);

    console.log("‚úÖ Î≥ëÌï©Îêú Ï∂©Ï†ÑÏÜå Í∞úÏàò:", stationList.length);
    console.log("üß™ ÏòàÏãú Ïä§ÌÖåÏù¥ÏÖò:", stationList[0]);

    return stationList;
  } catch (err) {
    console.error("SafeMap API Error:", err);
    throw new Error("Ïã§Ï†ú API Ìò∏Ï∂ú Ïã§Ìå®");
  }
}

export const EvDataService = {
  async getStations() {
    return await fetchRealStations();
  },

  filterStations(stations, filters) {
    return stations; // ÌïÑÌÑ∞Îäî ÏïÑÏßÅ ÏóÜÏùå
  },
};
