// src/pages/Map.js
import React, { useEffect, useState } from "react";
import { MapContainer, TileLayer } from "react-leaflet";
import "leaflet/dist/leaflet.css";

import MapMarker from "../components/ev/MapMarker";
import StationCard from "../components/ev/StationCard";
import StationDetailPanel from "../components/ev/StationDetailPanel";

export default function MapPage() {
  const [stations, setStations] = useState([]);
  const [filteredStations, setFilteredStations] = useState([]);
  const [selectedStation, setSelectedStation] = useState(null);

  const [loading, setLoading] = useState(true);

  // ‚≠ê ÎÑ§ SafeMap Ïù∏Ï¶ùÌÇ§ ÏûÖÎ†•
  const API_KEY = "P0QJKBPH-P0QJ-P0QJ-P0QJ-P0QJKBPHWJ";

  // ‚≠ê SafeMap Ï∂©Ï†ÑÏÜå Îç∞Ïù¥ÌÑ∞(JSON) API
  const API_URL = `https://safemap.go.kr/openApiService/data/getChargingStationData1.do?serviceKey=P0QJKBPH-P0QJ-P0QJ-P0QJ-P0QJKBPHWJ&pageNo=1&numOfRows=2000&type=json`;

  // --------------------------------------------------
  // FETCH SAFE MAP API
  // --------------------------------------------------
  useEffect(() => {
    async function load() {
      try {
        setLoading(true);

        const res = await fetch(API_URL);
        const json = await res.json();

        // ÏïàÏ†ÑÌïòÍ≤å Îç∞Ïù¥ÌÑ∞ Ï†ëÍ∑º
        const list = json?.list || [];

        // üî• Map.jsÍ∞Ä ÏõêÌïòÎäî Îç∞Ïù¥ÌÑ∞ Íµ¨Ï°∞Î°ú Î≥ÄÌôò
        const mapped = list.map((item, index) => ({
          id: index,
          name: item.faclNm,
          address: item.addr,
          latitude: Number(item.lat),
          longitude: Number(item.lon),
          status: "available",
        }));

        setStations(mapped);
        setFilteredStations(mapped);

        setLoading(false);
      } catch (e) {
        console.error("SafeMap API Error:", e);
        setLoading(false);
      }
    }

    load();
  }, []);

  // --------------------------------------------------
  // LOADING
  // --------------------------------------------------
  if (loading) {
    return (
      <div className="flex items-center justify-center h-screen text-slate-500 font-medium">
        Ï∂©Ï†ÑÏÜå Îç∞Ïù¥ÌÑ∞Î•º Î∂àÎü¨Ïò§Îäî Ï§ë...
      </div>
    );
  }

  // --------------------------------------------------
  // RENDER
  // --------------------------------------------------
  return (
    <div className="flex w-full h-screen bg-gray-100 overflow-hidden">
      {/* LEFT ‚Äî LIST */}
      <div className="w-96 h-full overflow-y-auto bg-white border-r border-gray-200">
        <div className="divide-y divide-gray-100">
          {filteredStations.map((station) => (
            <div key={station.id} className="p-4">
              <StationCard
                station={station}
                compact={true}
                onSelect={() => setSelectedStation(station)}
                isFavorite={false}
              />
            </div>
          ))}
        </div>
      </div>

      {/* RIGHT ‚Äî MAP */}
      <div className="flex-1 relative">
        <MapContainer
          center={[37.5665, 126.9780]} // ÏÑúÏö∏ Ï¢åÌëú
          zoom={12}
          style={{ width: "100%", height: "100%" }}
        >
          {/* Í∏∞Î≥∏ ÏßÄÎèÑ */}
          <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />

          {/* üî• Ï∂©Ï†ÑÏÜå ÎßàÏª§ */}
          {filteredStations.map((station) => (
            <MapMarker
              key={station.id}
              station={{
                ...station,
                lat: station.latitude,
                lng: station.longitude,
              }}
              onClick={() => setSelectedStation(station)}
            />
          ))}
        </MapContainer>

        {/* DETAIL PANEL */}
        {selectedStation && (
          <StationDetailPanel
            station={selectedStation}
            onClose={() => setSelectedStation(null)}
          />
        )}
      </div>
    </div>
  );
}
