import React, { useEffect, useState } from "react";
import { MapContainer, TileLayer } from "react-leaflet";
import "leaflet/dist/leaflet.css";

import { EvDataService } from "../components/ev/EvDataService";
import MapMarker from "../components/ev/MapMarker";

export default function Map() {
  const [stations, setStations] = useState([]);
  const [filteredStations, setFilteredStations] = useState([]);
  const [loading, setLoading] = useState(true);

  // -------------------------------
  // üî• Load EV Stations
  // -------------------------------
  useEffect(() => {
    async function load() {
      setLoading(true);

      const data = await EvDataService.getStations();

      console.log("üöÄ Î≥ÄÌôòÎêú Ï†ÑÏ≤¥ Ïä§ÌÖåÏù¥ÏÖò Îç∞Ïù¥ÌÑ∞:", data);

      setStations(data);
      setFilteredStations(data);

      setLoading(false);
    }

    load();
  }, []);

  if (loading) {
    return <div className="p-4">Î°úÎî© Ï§ë‚Ä¶</div>;
  }

  return (
    <div className="w-full h-screen">
      <MapContainer
        center={[37.55, 126.97]}
        zoom={13}
        style={{ width: "100%", height: "100%" }}
      >
        <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />

        {/* üî• ÎßàÏª§ Ï∞çÍ∏∞ ÏßÅÏ†Ñ Ï¢åÌëú ÌôïÏù∏ */}
        {filteredStations.map((s) => {
          console.log("üß≠ Marker Ï¢åÌëú:", s.lat, s.lng, s.name);
          return <MapMarker key={s.id} station={s} />;
        })}
      </MapContainer>
    </div>
  );
}
