// src/components/ev/EvDataService.js
import proj4 from "proj4";

export async function fetchRealStations() {
  try {
    const response = await fetch("http://localhost:4000/api/charging");
    const json = await response.json();
    const items = json?.response?.body?.items?.item;
    if (!items) return [];

    // ‚≠ê Safemap Ï¢åÌëúÍ≥Ñ = KATEC (EPSG:5179)
    const KATEC =
      "+proj=tmerc +lat_0=38 +lon_0=127 +k=0.9996 +x_0=200000 +y_0=500000 +ellps=GRS80 +units=m +no_defs";

    const WGS84 = "+proj=longlat +datum=WGS84 +no_defs";

    return items.map((item) => {
      const x = Number(item.X);
      const y = Number(item.Y);

      console.log("üìå ÏõêÎ≥∏ Ï¢åÌëú:", x, y);

      // ‚≠ê Î∞òÎìúÏãú EPSG:5179 ‚Üí WGS84 Î≥ÄÌôò
      const [lng, lat] = proj4(KATEC, WGS84, [x, y]);

      console.log("üéØ Î≥ÄÌôòÎêú Ï¢åÌëú:", lat, lng);

      return {
        id: item.OBJT_ID,
        name: item.STAT_NM,
        address: item.ADRES,
        company: item.BUSI_NM,
        chargerType: item.CHGER_TY,
        lat,
        lng,
        status: "available",
      };
    });
  } catch (err) {
    console.error("API Error:", err);
    return [];
  }
}

export const EvDataService = {
  async getStations() {
    return await fetchRealStations();
  },
  filterStations(stations, filters) {
    return stations;
  }
};
