import proj4 from "proj4";

export async function fetchRealStations() {
  try {
    const response = await fetch("http://localhost:4000/api/charging");
    const json = await response.json();

    const items = json?.response?.body?.items?.item;
    if (!items) return [];

    // Ï¢åÌëú Î≥ÄÌôò Ï†ïÏùò
    const TM_5179 =
      "+proj=tmerc +lat_0=38 +lon_0=127 +k=1 +x_0=200000 +y_0=500000 +ellps=GRS80 +units=m +no_defs";
    const WGS84 = "+proj=longlat +datum=WGS84 +no_defs";

    // üìå Î≥ÄÌôò Ïã§Ìñâ
    return items.map((item) => {
      console.log("üìå ÏõêÎ≥∏ Ï¢åÌëú:", item.X, item.Y);

      const [lng, lat] = proj4(TM_5179, WGS84, [
        Number(item.X),
        Number(item.Y),
      ]);

      console.log("‚û°Ô∏è Î≥ÄÌôòÎêú Ï¢åÌëú:", lat, lng);

      return {
        id: item.OBJT_ID,
        name: item.STAT_NM,
        address: item.ADRES,
        company: item.BUSI_NM,
        chargerType: item.CHGER_TY,
        status: "available", // safemapÏóê ÏÉÅÌÉúÏ†ïÎ≥¥Îäî ÏóÜÏùå ‚Üí Í∏∞Î≥∏Í∞í ÏßÄÏ†ï

        latitude: lat,
        longitude: lng,
      };
    });
  } catch (err) {
    console.error("SafeMap API Error:", err);
    throw new Error("Ïã§Ï†ú API Ìò∏Ï∂ú Ïã§Ìå®");
  }
}

export const EvDataService = {
  async getStations() {
    return await fetchRealStations();
  },

  filterStations(stations, filters) {
    return stations.filter((s) => {
      return true; // ÏßÄÍ∏àÏùÄ ÌïÑÌÑ∞ ÏóÜÏùå
    });
  },
};
