const R = 6378137;

function webMercatorToLatLng(x, y) {
  const lon = (x / R) * (180 / Math.PI);
  const lat =
    (2 * Math.atan(Math.exp(y / R)) - Math.PI / 2) * (180 / Math.PI);

  return { lat, lng: lon };
}

export async function fetchRealStations() {
  try {
    const response = await fetch("http://localhost:4000/api/charging");
    const json = await response.json();

    const items = json?.response?.body?.items?.item;
    if (!items) {
      console.warn("⚠️ Safemap items 없음:", json);
      return [];
    }

    //동일 충전소 병합  
    const merged = {};

    for (const item of items) {
      const x = Number(item.X);
      const y = Number(item.Y);

      if (!x || !y || Number.isNaN(x) || Number.isNaN(y)) continue;

      const { lat, lng } = webMercatorToLatLng(x, y);

      if (lat < 30 || lat > 45 || lng < 120 || lng > 135) continue;

      const key = item.STAT_ID;

      if (!merged[key]) {
        merged[key] = {
          id: key,
          name: item.STAT_NM,
          address: item.ADRES,
          company: item.BUSI_NM,
          lat,
          lng,
          chargers: [],
        };
      }

      merged[key].chargers.push({
        chargerId: item.CHGER_ID,
        chargerType: item.CHGER_TY,
        useTime: item.USE_TM,
        tel: item.TELNO,
      });
    }

    const stationList = Object.values(merged);

    return stationList;
  } catch (err) {
    console.error("SafeMap API Error:", err);
    throw new Error("실제 API 호출 실패");
  }
}

export const EvDataService = {
  async getStations() {
    return await fetchRealStations();
  },

  filterStations(stations, filters) {
    return stations; 
  },
};
