// EvDataService.js
export async function fetchRealStations() {
  try {
    const res = await fetch("http://localhost:4000/api/charging");
    const json = await res.json();

    const items = json?.response?.body?.items?.item;
    if (!items) return [];

    // XML â†’ ì •ê·œí™” ë°ì´í„° ë³€í™˜
    return items.map((item) => ({
      id: item.OBJT_ID,
      name: item.STAT_NM,
      address: item.ADRES,
      latitude: Number(item.Y),
      longitude: Number(item.X),
      chargerType: item.CHGER_TY,
      company: item.BUSI_NM,
      status: "available",
    }));
  } catch (err) {
    console.error("ðŸ”¥ SafeMap API Error:", err);
    return [];
  }
}

// â­â­â­ ë°˜ë“œì‹œ ì¶”ê°€í•´ì•¼ í•˜ëŠ” í•„í„°ë§ í•¨ìˆ˜!!
function filterStations(stations, filters) {
  if (!Array.isArray(stations)) return [];

  return stations.filter((station) => {
    const matchStatus =
      filters.statuses.length === 0 ||
      filters.statuses.includes(station.status);

    const matchType =
      filters.chargerTypes.length === 0 ||
      filters.chargerTypes.includes(station.chargerType);

    const matchCompany =
      filters.companies.length === 0 ||
      filters.companies.includes(station.company);

    return matchStatus && matchType && matchCompany;
  });
}

export const EvDataService = {
  getStations: fetchRealStations,
  filterStations, 
};
