import React, { useState, useEffect } from "react";
import { MapContainer, TileLayer } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import { ChevronLeft, ChevronRight } from "lucide-react";

import { EvDataService } from "../components/ev/EvDataService";
import MapMarker from "../components/ev/MapMarker";
import StationCard from "../components/ev/StationCard";
import StationDetailPanel from "../components/ev/StationDetailPanel";

export default function Map() {
  const [stations, setStations] = useState([]);
  const [selectedStation, setSelectedStation] = useState(null);

  const [sidebarOpen, setSidebarOpen] = useState(true);

  useEffect(() => {
    async function load() {
      const data = await EvDataService.getStations();
      setStations(data);
    }
    load();
  }, []);

  return (
    <div className="relative w-full h-screen flex overflow-hidden">

      {/* ⭐ 사이드바 */}
      <div
        className={`transition-all duration-300 bg-white border-r shadow-sm h-full
          ${sidebarOpen ? "w-96 opacity-100" : "w-0 opacity-0 pointer-events-none"}
        `}
      >
        <div className="w-96 p-4 overflow-y-auto">
          {stations.map((station) => (
            <div key={station.id} className="mb-2">
              <StationCard
                station={station}
                compact={true}
                onSelect={() => setSelectedStation(station)}
              />
            </div>
          ))}
        </div>
      </div>

      {/* ⭐ 사이드바 토글 버튼 */}
      {!sidebarOpen && (
        <button
          className="
            absolute left-0 top-5 z-[9999]
            bg-white border shadow-md
            p-2 rounded-r-xl
            hover:bg-slate-100
          "
          onClick={() => setSidebarOpen(true)}
        >
          <ChevronRight className="w-5 h-5 text-slate-700" />
        </button>
      )}

      {sidebarOpen && (
        <button
          className="
            absolute left-96 top-5 z-[9999]
            bg-white border shadow-md
            p-2 rounded-r-xl
            hover:bg-slate-100
          "
          onClick={() => setSidebarOpen(false)}
        >
          <ChevronLeft className="w-5 h-5 text-slate-700" />
        </button>
      )}

      {/* ⭐ 지도 */}
      <div className="flex-1 relative">

        <MapContainer
          center={[37.5665, 126.9780]}
          zoom={13}
          zoomControl={true}
          style={{ width: "100%", height: "100%" }}
        >
          <TileLayer url="https://tile.openstreetmap.org/{z}/{x}/{y}.png" />

          {stations.map((s) => (
            <MapMarker
              key={s.id}
              station={{ ...s, status: "available" }}
              onClick={() => setSelectedStation(s)}
            />
          ))}
        </MapContainer>

        {/* 상세 패널 */}
        {selectedStation && (
          <StationDetailPanel
            station={selectedStation}
            onClose={() => setSelectedStation(null)}
          />
        )}
      </div>
    </div>
  );
}
