// src/pages/MapPage.js
import React, { useState, useEffect } from "react";
import { MapContainer, TileLayer } from "react-leaflet";
import "leaflet/dist/leaflet.css";

import { EvDataService } from "../components/ev/EvDataService";
import { MockApiService } from "../components/ev/MockApiService";

import StationCard from "../components/ev/StationCard";
import StationDetailPanel from "../components/ev/StationDetailPanel";
import MapMarker from "../components/ev/MapMarker";
import FilterPanel from "../components/ev/FilterPanel";

export default function MapPage() {
  const [stations, setStations] = useState([]);
  const [filteredStations, setFilteredStations] = useState([]);
  const [selectedStation, setSelectedStation] = useState(null);

  const [filters, setFilters] = useState({
    statuses: [],
    chargerTypes: [],
    companies: [],
  });

  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [loading, setLoading] = useState(true);

  // -----------------------------
  // üî• Îç∞Ïù¥ÌÑ∞ Î°úÎìú
  // -----------------------------
  useEffect(() => {
    async function load() {
      setLoading(true);
      const data = await EvDataService.getStations();

      console.log("üìå Î∂àÎü¨Ïò® Ïä§ÌÖåÏù¥ÏÖò:", data.length);
      setStations(data);
      setFilteredStations(data);

      setLoading(false);
    }

    load();
  }, []);

  // -----------------------------
  // üîç ÌïÑÌÑ∞ Ï†ÅÏö©
  // -----------------------------
  useEffect(() => {
    const result = EvDataService.filterStations(stations, filters);
    setFilteredStations(result);
  }, [stations, filters]);

  if (loading) {
    return (
      <div className="flex items-center justify-center h-screen text-slate-600">
        Î°úÎî© Ï§ë...
      </div>
    );
  }

  return (
    <div className="w-full h-screen flex overflow-hidden relative bg-white">

      {/* ============================== */}
      {/* üîò ÏÇ¨Ïù¥ÎìúÎ∞î ÌÜ†Í∏Ä Î≤ÑÌäº */}
      {/* ============================== */}
      <button
        onClick={() => setSidebarOpen(!sidebarOpen)}
        className={`
          absolute top-4 z-[9999] transition-all duration-300
          bg-white shadow-lg rounded-r-xl border border-slate-200
          flex items-center justify-center
          w-10 h-14 hover:bg-slate-100

          ${sidebarOpen
            ? "left-[360px] rounded-l-xl"  // ÏÇ¨Ïù¥ÎìúÎ∞î Ïó¥Î¶º
            : "-left-1"                    // Îã´Ìûò ‚Üí Ï±ÖÍ∞àÌîºÏ≤òÎüº Î≥¥ÏûÑ
          }
        `}
      >
        {sidebarOpen ? "‚Äπ" : "‚Ä∫"}
      </button>

      {/* ============================== */}
      {/* üìå LEFT SIDEBAR */}
      {/* ============================== */}
      <div
        className={`
          h-full bg-white border-r border-slate-200 overflow-y-auto transition-all duration-300
          ${sidebarOpen ? "w-[360px]" : "w-0 pointer-events-none"}
        `}
      >
        {sidebarOpen && (
          <div className="w-[360px]">
            {/* ÌïÑÌÑ∞ Ìå®ÎÑê */}
            <div className="p-4 border-b border-gray-200">
              <FilterPanel filters={filters} onChange={setFilters} />
            </div>

            {/* Ïä§ÌÖåÏù¥ÏÖò Î¶¨Ïä§Ìä∏ */}
            <div className="divide-y divide-gray-100">
              {filteredStations.map((station) => (
                <div key={station.id} className="p-2">
                  <StationCard
                    compact
                    station={station}
                    onSelect={() => setSelectedStation(station)}
                  />
                </div>
              ))}
            </div>
          </div>
        )}
      </div>

      {/* ============================== */}
      {/* üìå RIGHT MAP AREA */}
      {/* ============================== */}
      <div className="flex-1 relative overflow-hidden">

        <MapContainer
          center={[37.5665, 126.9780]}
          zoom={13}
          style={{ width: "100%", height: "100%" }}
        >
          <TileLayer
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />

          {/* ÎßàÏª§ */}
          {filteredStations.map((station) => (
            <MapMarker
              key={station.id}
              station={station}
              onClick={() => setSelectedStation(station)}
            />
          ))}
        </MapContainer>

        {/* ÏÉÅÏÑ∏ Ìå®ÎÑê */}
        {selectedStation && (
          <StationDetailPanel
            station={selectedStation}
            onClose={() => setSelectedStation(null)}
          />
        )}
      </div>
    </div>
  );
}
