export async function fetchRealStations() {
  try {
    const res = await fetch("http://localhost:4000/api/charging");
    const json = await res.json();

    const items = json?.response?.body?.items?.item;
    if (!items) return [];

    console.log("ðŸ“Œ ì›ë³¸ ì¢Œí‘œ:", item.X, item.Y);
    const [lng, lat] = proj4(TM_5179, WGS84, [
      Number(item.X),
      Number(item.Y),
    ]);
    console.log("âž¡ï¸ ë³€í™˜ëœ ì¢Œí‘œ:", lat, lng);


    // â­ SafeMap ì€ X, Y â†’ TM ì¢Œí‘œê³„ë¥¼ ì¤Œ â†’ WGS84 ë¡œ ë³€í™˜
    return items.map((item) => {
      // ì¢Œí‘œ ë³€í™˜
      const [lng, lat] = proj4(TM_5179, WGS84, [
        Number(item.X),
        Number(item.Y),
      ]);

      return {
        id: item.OBJT_ID,
        name: item.STAT_NM,
        address: item.ADRES,
        latitude: lat,
        longitude: lng,
        chargerType: item.CHGER_TY,
        company: item.BUSI_NM,
        status: "available",
      };
    });
  } catch (err) {
    console.error("ðŸ”¥ SafeMap API Error:", err);
    return [];
  }
}

/* --------------------------------------
   EvDataService ê°ì²´ (MapPage ì—ì„œ ì‚¬ìš©)
