export const EvDataService = {
  // 1) ëª¨ë“  ì¶©ì „ì†Œ ê°€ì ¸ì˜¤ê¸°
  async getStations() {
    return await fetchRealStations();
  },

  // 2) í•„í„°ë§ ê¸°ëŠ¥
  filterStations(stations, filters) {
    if (!Array.isArray(stations)) return [];

    return stations.filter((s) => {
      const matchStatus =
        filters.statuses.length === 0 ||
        filters.statuses.includes(s.status);

      const matchChargerType =
        filters.chargerTypes.length === 0 ||
        filters.chargerTypes.includes(String(s.chargerType));

      const matchCompany =
        filters.companies.length === 0 ||
        filters.companies.includes(s.company);

      return matchStatus && matchChargerType && matchCompany;
    });
  },
};
